#!/bin/python3# Paulo Elienay II# importsimport os, sys# varsname = ''notes = []# defsdef clear():    os.system('clear')def get_profile(mode):    return open("profile", mode)def get_name():    return get_profile("r").readlines()[0]def check_for_profile():    return os.path.exists("./profile")def create_profile(name):    profile = get_profile("w")    profile.write(name + "\n")    profile.close()def get_notes_list():    return get_profile("r").readlines()def add_note():    new_note = input("Enter your note: ")    profile = get_profile("a")    profile.write(new_note + "\n")    profile.close()    main_menu()def main_menu():    clear()    if len(get_notes_list()) > 1:        i = 1        notes_num = len(get_notes_list())-1        while i <= notes_num:            print(f"[{get_name().strip()}]: " + get_notes_list()[i].strip())            i = i + 1        user_input = input("Do you want to create another note? [yes/no] ")        if user_input == "yes":            add_note()        else:            print("Nothing left to do. Exiting")            sys.exit()    else:        print("No notes in the database.")        user_input = input("Do you want to create your first note? [yes/no] ")        if user_input == "yes":            add_note()        else:            print("Nothing left to do. Exiting")            sys.exit()def write_profile(new_profile):    new_profile.close()# maindef main():    print("Checking for profile...")    if check_for_profile():        print("A profile already exists.")        print("Hi {}".format(get_name().strip()))        main_menu()    else:        print("No profile found.")        print("Creating new profile.")        create_profile(input("What's your name? "))        print("Hi {}".format(get_name().strip()))        main_menu()if __name__ == '__main__':    clear()    main()